export default function Icon({
  name,
  size = 20,
  className,
  style,
}: IconProps) {
  const IconComponent = icons[name];

  if (!IconComponent) {
    return null;
  }

  return (
    <div
      style={{
        width: size,
        height: size,
        display: 'inline-flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexShrink: 0,
        ...style,
      }}
      className={className}
    >
      <IconComponent />
    </div>
  );
}
